private suspend fun insertArticles1(json: String) {
    val fromJson = Gson().fromJson(json, Wanandroid::class.java)
    val articles = fromJson.data.datas.map {
        Article(
            userId = 1,
            username = "cgf",
            author = "Chen Guofei",
            title = it.title,
            link = it.link,
            niceDate = "2019-11-15 21:22:22",
            visibleMode = VisibleMode.PUBLIC,
            tags = it.tags.map { tag ->
                tag.name
            }.toTypedArray(),
            likes = 0,
            collections = 0,
            comments = 0,
            pictures = arrayOf("https://img2.baidu.com/it/u=1845414853,1051354461&fm=253&app=120&size=w931&n=0&f=JPEG&fmt=auto?sec=1709312400&t=b12ab73b6e508f4708bb17eca50235c1")
        )
    }
    "toInsert=$articles".logd("insert_articles")
    "articles size=${articles.size}".logd("insert_articles")
    val articleDao = ArticleDaoImpl()
    val insertIds = articleDao.insertBatch(articles)
    "insert success. ids=$insertIds".logd("insert_articles")
